import React from "react";
import Nav from '../components/navbar';
import Foot from '../components/footer';

class Contact extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      fullname: "John Transmission",
      email: "example@gmail.com",
      askQuestion: "Ask us anything you like"
    };

    this.handleInputChange = this.handleInputChange.bind(this);
  }

  handleInputChange(event) {
    const target = event.target;
    const value = target.value;
    const name = target.name;

    this.setState({
      [name]: value
    });
  }

  render() {
    return (
      <div id="wrapper">
        <Nav />
        <div id="banner">
          <img src="/background_phone.jpg" />
        </div>
        <div id="contactwrapper">
          <form>
            <label>Full Name
              <input
              type="text"
              value={this.state.fullname}
              onChange={handleInputChange}
              ></input>
            </label>
          </form>
        </div>
        <h2 >Contact Us</h2>
        <Foot />
        <style jsx>{`
          body {
            margin: 0;
          }
          #banner {
            float: left;
            width: 100%;
            height: 350px;
            overflow: hidden;
            position: relative;
          }
          #banner img {
            float: left;
            width: 100%;
            height: auto;
            margin-top: -100px;
          }
          #wrapper {
            float: left;
            margin: 0;
            width: 100%;
          }
          h2 {
            float: left;
            display: block;
          }
          `}</style>
      </div>
    );
  }
}

export default Contact;
