import Link from 'next/link';

const Foot = () => (
  <div id="footer">
    <ul>
      <li><Link href="/"><a>- Back to Home</a></Link></li>
      <li><Link href="/contact"><a>- Get In Touch</a></Link></li>
      <li><Link href="/about"><a>- Learn About Us</a></Link></li>
    </ul>
    <div id="acreditations">
      <ol>
        <li>Website Author: <a>Will Forte</a></li>
        <li>Powered By: <a href="https://reactjs.org/">React</a></li>
      </ol>
    </div>
    <style jsx>{`
      #footer {
        float: left;
        width: 80%;
        background: rgba(0,0,0,.4);
        padding: 10%;
      }
      ul {
        float: left;
        width: 40%;
        padding-top: 20px;
        padding-bottom: 30px;
        box-shadow: 0 0 2px rgba(0,0,0.2);
      }
      #acreditations {
        float: right;
        width: 35%;
        margin-right: 10%;
      }
      ol {
        float: left;
        width: 100%;
      }
      ol li {
        float: left;
        width: 100%;
        margin-top: 25px;
        font: 14px 'Rubik';
      }
      ol li a {
        width: 40%;
        padding: 5%;
        margin: 0;
        margin-top: -20px;
        float: right;
        text-align: center;
      }
      ol li a:hover {
        cursor: pointer;
      }
      li {
        float: left;
        width: 100%;
        color: white;
      }
      li a {
        border-bottom: 2px solid rgba(0,0,0,0);
      }
      li a:hover {
        color: rgba(255,255,255,.6);
        background: none;
        border-bottom: 2px solid #FF6161;
      }

/*  ||||| Mobile device display properties only |||||  */

      @media only screen and (max-device-width: 480px) {
        ul {
          float: left;
          width: 100%;
          position: relative;
          background: none;
        }
        li {
          float: left;
          width: 100%;
        }
        li a {
          float: left;
        }
      }
      `}</style>
  </div>
);

export default Foot;
