import React, { Component } from "react";
import Nav from '../components/navbar';
import Foot from '../components/footer';

class Contact extends Component {
  constructor(props) {
    super(props);
    this.state = {
      fullname: "John Transmission",
      email: "example@gmail.com",
      askQuestion: "Ask us anything you like",
      problem: "Not Working"
    };

    this.handleInputChange = this.handleInputChange.bind(this);
    this.handleClear = this.handleClear.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleInputChange(event) {
    const target = event.target;
    const value = target.value;
    const name = target.name;
    this.setState({
      [name]: value
    });
  }

  handleClear(event) {
    this.setState({
      [event.target.name]: ''
    });
  }

  handleSubmit(event) {
    alert('Name: ' + this.state.fullname + ' Email: ' + this.state.email + ' Request: ' + this.state.askQuestion);
    event.preventDefault();
  }

  render() {
    return (
      <div id="wrapper">
        <Nav />
        <div id="banner">
          <img src="/background_phone.jpg" />
        </div>
        <h2>Contact Us</h2>
        <div id="contactwrapper">
          <form onSubmit={this.handleSubmit}>
            <label>Full Name
              <input
              type="text"
              name="fullname"
              value={this.state.fullname}
              onChange={this.handleInputChange}
              onClick={this.handleClear}
              />
            </label>
            <label>Email
              <input
              type="email"
              name="email"
              value={this.state.email}
              onChange={this.handleInputChange}
              onClick={this.handleClear}
              />
            </label>
            <label>What can we help you with?
              <textarea
              name="askQuestion"
              value={this.state.askQuestion}
              onChange={this.handleInputChange}
              onClick={this.handleClear}
              ></textarea>
            </label>
            <label>Common Implications
              <select name="problem" onChange={this.handleInputChange} value={this.state.problem}>
                <option value="check_engine">Check Engine Light</option>
                <option value="transmission">Transmission Problem</option>
                <option value="tires">Tires/Break Repair</option>
              </select>
            </label>
            <button type="submit" value="submit">Submit</button>
          </form>
        </div>

        <Foot />
        <style jsx>{`
          body {
            margin: 0;
          }
          #banner {
            float: left;
            width: 100%;
            height: 350px;
            overflow: hidden;
            position: relative;
          }
          #contactwrapper {
            float: left;
            width: 80%;
            padding: 10%;
            padding-top: 0;
          }
          form {
            float: left;
            width: 80%;
            padding: 10%;
            padding-top: 5%;
            box-shadow: 0 0 10px rgba(150,150,150.02);
          }
          button {
            float: right;
            padding: 15px;
            margin-top: 20px;
            background: rgba(255,61,61,.7);
            border: none;
            color: white;
            font: 15px 'Mukta';
            box-shadow: 0 0 2px rgba(150,150,150.02);
            cursor: pointer;
          }
          button:hover {
            background: rgba(255,61,61,.5);
          }
          label {
            width: 100%;
            float: left;
            font: 20px 'Mukta';
            margin-top: 20px;
          }
          input {
            float: right;
            font: 14px 'Mukta';
            color: rgba(89,89,89,.6);
            padding: 5px;
          }
          select {
            float: right;
            margin: 10px;
          }
          option {
            float: left;
            padding: 10px;
          }
          input:hover {
            background: rgba(150,150,150,.1);
          }
          textarea {
            float: right;
            width: 50%;
            height: 150px;
            font: 14px 'Mukta';
            padding: 5px;
            color: rgba(89,89,89,.6);
          }
          textarea:hover {
            background: rgba(150,150,150,.1);
          }
          #banner img {
            float: left;
            width: 100%;
            height: auto;
            margin-top: -100px;
          }
          #wrapper {
            float: left;
            margin: 0;
            width: 100%;
          }
          h2 {
            float: left;
            display: block;
            padding: 20px;
            margin-left: 30px;
            font: 40px 'Mukta';
          }
          @media only screen and (max-device-width: 480px) {
            #banner img {
              margin-top: 0;
              width: auto;
              height: 100%;
            }
            input {
              width: 100%;
              float: left;
              background: rgba(150,150,150,.1);
            }
            textarea {
              width: 100%;
              float: left;
              background: rgba(150,150,150,.1);
            }
            select {
              background: rgba(150,150,150,.1);
              width: 100%;
            }
          }
          `}</style>
      </div>
    );
  }
}

export default Contact;
