import React, { Component } from 'react';

function FormConfirmation(props) {
  return(
    <>
      <div id="formwrapper" className={props.show}>
        <h2>Name: {props.name}</h2>
        <h2>Email: {props.email}</h2>
        <h2>Request: {props.req}</h2>
        <p>Thank you so much {props.name}, we will respond to your request/question as soon as possible</p>
        <style jsx>{`
          #formwrapper {
            float: left;
            width: 80%;
            padding: 10%;
            background: rgba(200,200,200,.2);
          }
          h2 {
            float: left;
            width: 100%;
            font: 16px arial;
          }
          p {
            color: #64FF00;
            font: 20px arial;
          }
          .submit {
            display: block;
          }
          .false {
            display: none;
          }
          `}</style>
      </div>
    </>
  );
}

export default FormConfirmation;
