import React, { Component } from "react";
import Nav from '../components/navbar';
import Foot from '../components/footer';

class Contact extends Component {
  constructor(props) {
    super(props);
    this.state = {
      fullname: "John Transmission",
      email: "example@gmail.com",
      askQuestion: "Ask us anything you like"
    };

    this.handleInputChange = this.handleInputChange.bind(this);
    this.handleClear = this.handleClear.bind(this);
  }

  handleInputChange(event) {
    const target = event.target;
    const value = target.value;
    const name = target.name;
    this.setState({
      [name]: value
    });
  }

  handleClear(event) {
    this.setState({
      [event.target.name]: ''
    });
  }

  render() {
    return (
      <div id="wrapper">
        <Nav />
        <div id="banner">
          <img src="/background_phone.jpg" />
        </div>
        <h2 >Contact Us</h2>
        <div id="contactwrapper">
          <form>
            <label>Full Name
              <input
              type="text"
              name="fullname"
              value={this.state.fullname}
              onChange={this.handleInputChange}
              onClick={this.handleClear}
              />
            </label>
            <label>Email
              <input
              type="email"
              name="email"
              value={this.state.email}
              onChange={this.handleInputChange}
              onClick={this.handleClear}
              />
            </label>
            <label>What can we help you with?
              <textarea
              name="askQuestion"
              value={this.state.askQuestion}
              onChange={this.handleInputChange}
              onClick={this.handleClear}
              ></textarea>
            </label>
          </form>
        </div>

        <Foot />
        <style jsx>{`
          body {
            margin: 0;
          }
          #banner {
            float: left;
            width: 100%;
            height: 350px;
            overflow: hidden;
            position: relative;
          }
          #contactwrapper {
            float: left;
            width: 80%;
            padding: 10%;
          }
          form {
            float: left;
            width: 80%;
            padding: 10%;
          }
          label {
            width: 100%;
            float: left;
            font: 20px 'Mukta';
            margin-top: 20px;
          }
          input {
            float: right;
            font: 14px 'Mukta';
            color: rgba(89,89,89,.6);
            padding: 5px;
          }
          textarea {
            float: right;
            width: 50%;
            height: 150px;
            font: 14px 'Mukta';
            padding: 5px;
            color: rgba(89,89,89,.6);
          }
          #banner img {
            float: left;
            width: 100%;
            height: auto;
            margin-top: -100px;
          }
          #wrapper {
            float: left;
            margin: 0;
            width: 100%;
          }
          h2 {
            float: left;
            display: block;
          }
          `}</style>
      </div>
    );
  }
}

export default Contact;
