import React from "react";
import { Helmet } from 'react-helmet';
import Nav from "../components/navbar";
import Link from 'next/link';
import Foot from '../components/footer';

export default function Index() {
  return (
    <div id="wrapper">
      <Helmet>
        <title>J and M Transmission</title>
        <meta name="J and M Transmission" content="Come in and get your transmission fixed, car repair, friendly service Attleboro MA" />
      </Helmet>
      <Nav />
      <div id="banner">
        <img src="/background_engine.jpg" />
      </div>
      <div id="aboutcomp">
        <img src="/family.jpeg" />
        <h1>About the Company</h1>
        <p>Ready to get your transmission fixed? You have come to right place, down at J and M transmissions we treat you like family because we know how important it is to get your car back on the road, you can <Link href="/contact"><a id="pagelink"> contact us </a></Link> about prices and scheduling if you would like to know more!</p>

      </div>
      <div id="locationcontainer">
        <div id="getintouch">
          <ul>
            <li>Address: 1076 Pleasant St, Attleboro, MA, 02703</li>
            <li>Shop Phone: 1-(508)-222-1406</li>
            <li>Shop Owner: Steven Dalrymple</li>
          </ul>
        </div>
      </div>
      <iframe src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d2967.1299957503716!2d-71.24674358455779!3d41.954542768605826!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x89e460f75c90b6bb%3A0x63dc839649eda577!2sJ%20%26%20M%20Transmissions!5e0!3m2!1sen!2sus!4v1586143162576!5m2!1sen!2sus" width="400" height="400" frameBorder="0" allowFullScreen="" aria-hidden="false" tabIndex="0"></iframe>

      <Foot />
      <style jsx>{`
        @import url('https://fonts.googleapis.com/css2?family=Rubik:wght@500&display=swap');
        #wrapper {
          float: left;
          width: 100%;
          background: white;
          margin-top: 0;
          margin: 0;
          padding: 0;
        }
        #aboutcomp {
          float: left;
          width: 100%;
          margin-top: 40px;
          margin-bottom: 40px;
        }
        #aboutcomp img {
          float: right;
          width: 230px;
          height: 230px;
          margin-top: 30px;
          margin-right: 40px;
          box-shadow: 0 0 10px rgba(20,20,20,.4);
        }
        #locationcontainer {
          float: left;
          width: 80%;
          background: rgba(89,89,89,.2);
          padding: 10%;
        }
        iframe {
          float: left;
          width: 100%;
        }
        #getintouch {
          float: left;
          width: 100%;
        }
        #getintouch ul {
          float: left;
          width: 100%;
          box-shadow: 0 0 5px rgba(200,200,200,.6);
        }
        #getintouch ul li {
          color: white;
          float: left;
          width: 100%;
          font: 20px 'Mukta';
        }
        #banner {
          float: left;
          width: 100%;
          height: 350px;
          overflow: hidden;
          position: relative;
        }
        h1 {
          float: left;
          padding: 30px;
          font: 40px 'Rubik';
          margin: 0;
          margin-top: 10px;
          border: none;
        }
        p {
          float: left;
          width: 60%;
          font: 18px arial;
          margin: 25px;
          padding: 10px;
          background: rgba(200,200,200, .2)
        }
        img {
          float: left;
          width: 100%;
        }

/* Mobile Styling for the index page (Only Execute if mobile-device) */
        @media only screen and (max-device-width: 480px) {
          p {
            width: 80%;
            padding: 10%;
            margin: 0;
          }
          img {
            height: 100%;
            width: auto;
          }
          #aboutcomp img {
            float: left;
            width: 80%;
            height: auto;
            margin: 10%;
            margin-top: 30px;
          }
          h1 {
            width: 80%;
            padding: 10%;
          }
          iframe {
            width: 100%;
          }
          #locationcontainer {
            padding: 0;
            margin: 0;
          }
        }
        `}</style>
    </div>
  );
}
