import React, { Component } from "react";
import { post } from '../services/restservice';
import { catchAxiosError } from "../services/error";

class Contact extends Component {
  constructor(props) {
    super(props);
    this.state = {
      fullname: "Jake Transmission",
      email: "jakeandmehe@example.com",
      askQuestion: "Ask us anything you like",
      problem: "Not Working",
      cellphone: "123-456-7890",
      confirmed: "false"
    };

    this.handleInputChange = this.handleInputChange.bind(this);
    this.handleClear = this.handleClear.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleInputChange(event) {
    const target = event.target;
    const value = target.value;
    const name = target.name;
    this.setState({
      [name]: value
    });
  }

  handleClear(event) {
    this.setState({
      [event.target.name]: ''
    });
  }

   handleSubmit(event) {
     this.setState({
       confirmed: 'submit'
     });
    const data = {
      name: this.state.fullname,
      email: this.state.email,
      cellphone: this.state.cellphone,
      message: this.state.askQuestion,
    };
    const sendingdata = new URLSearchParams(data);
    const res = post("/contact", sendingdata);
    if(res.error) {
      console.log(res);
    } else {
      console.log("Email sent");
    }
    event.preventDefault();
  }

  render() {
    return (
      <div id="wrapper">
        <div id="banner">
          <img src="/background_phone.jpg" />
        </div>
        <h2 className={this.state.confirmed}>Contact Us</h2>
        <div id="contactwrapper" className={this.state.confirmed}>
          <form onSubmit={this.handleSubmit} name="confirmed" value="submit">
            <label>Full Name
              <input
              type="text"
              name="fullname"
              value={this.state.fullname}
              onChange={this.handleInputChange}
              onClick={this.handleClear}
              />
            </label>
            <label>Email
              <input
              type="email"
              name="email"
              value={this.state.email}
              onChange={this.handleInputChange}
              onClick={this.handleClear}
              />
            </label>
            <label>Phone # (So we can get in touch!)
              <input
              type="cellphone"
              name="cellphone"
              value={this.state.cellphone}
              onChange={this.handleInputChange}
              onClick={this.handleClear}
              />
            </label>
            <label>What can we help you with?
              <textarea
              name="askQuestion"
              value={this.state.askQuestion}
              onChange={this.handleInputChange}
              onClick={this.handleClear}
              ></textarea>
            </label>
            <button type="submit" name="confirmed" value="submit">Submit</button>
          </form>
        </div>
        <style jsx>{`
          body {
            margin: 0;
          }
          #banner {
            float: left;
            width: 100%;
            height: 350px;
            overflow: hidden;
            position: relative;
          }
          #contactwrapper {
            float: left;
            width: 80%;
            padding: 10%;
            padding-top: 0;
          }
          .submit {
            display: none;
          }
          .false {
            display: block;
          }
          form {
            float: left;
            width: 80%;
            padding: 10%;
            padding-top: 5%;
            box-shadow: 0 0 10px rgba(200,200,200.02);
          }
          button {
            float: right;
            padding: 15px;
            margin-top: 50px;
            background: rgba(255,61,61,.7);
            border: none;
            color: white;
            font: 15px 'Mukta';
            box-shadow: 0 0 2px rgba(150,150,150.02);
            cursor: pointer;
          }
          button:hover {
            background: rgba(255,61,61,.5);
          }
          label {
            width: 100%;
            float: left;
            font: 20px 'Mukta';
            margin-top: 20px;
          }
          input {
            float: right;
            font: 14px 'Mukta';
            width: 30%;
            color: rgba(89,89,89,.6);
            padding: 10px;
            padding-left: 20px;
            padding-right: 20px;
          }
          select {
            float: right;
            margin: 10px;
          }
          option {
            float: left;
            padding: 10px;
          }
          input:hover {
            background: rgba(150,150,150,.1);
          }
          textarea {
            float: right;
            width: 50%;
            height: 150px;
            font: 14px 'Mukta';
            padding: 15px;
            color: rgba(89,89,89,.6);
          }
          textarea:hover {
            background: rgba(150,150,150,.1);
          }
          #banner img {
            float: left;
            width: 100%;
            height: auto;
            margin-top: -100px;
          }
          #wrapper {
            float: left;
            margin: 0;
            width: 100%;
          }
          h2 {
            float: left;
            display: block;
            padding: 20px;
            margin-left: 30px;
            font: 40px 'Mukta';
          }

/* Mobile exported version of the contact page to better format the view-port */
          @media only screen and (max-device-width: 480px) {
            #banner img {
              margin-top: 0;
              width: auto;
              height: 100%;
            }
            input {
              width: 80%;
              float: left;
              background: rgba(150,150,150,.1);
            }
            textarea {
              width: 90%;
              float: left;
              background: rgba(150,150,150,.1);
            }
            select {
              float: left;
              background: rgba(150,150,150,.1);
              width: 100%;
            }
          }
          `}</style>
      </div>
    );
  }
}

export default Contact;
