import Link from 'next/link';
import React, { useState } from "react";
import { Helmet } from 'react-helmet';

function Nav() {

  const [show, setShow] = useState("hide");

  const toggleDisplay = (e) => {
    e.preventDefault();
    if(show == "show") {
      setShow("hide");
    } else {
      setShow("show");
    }

  }

  return (
    <div id="navwrapper">
      <Helmet>
        <link rel="icon" href="/icon_gear_black.png" />
      </Helmet>
      <Link href="/">
        <img id="icon" src="/icon_gear.png" />
      </Link>
      <Link href="/">
        <a>J & M Transmission</a>
      </Link>
      <img src="/ham.png" id="hambutton" onClick={toggleDisplay} />
      <div id={show}>

        <ul id="dropdown" >
          <li><Link href="/contact">
            <a>Contact Us</a>
          </Link></li>
          <li><Link href="/">
            <a>About</a>
          </Link></li>
        </ul>
      </div>
      <style jsx global>{`
        @import url('https://fonts.googleapis.com/css2?family=Rubik:wght@500&display=swap');
        @import url('https://fonts.googleapis.com/css2?family=Mukta:wght@600&display=swap');
        body {
          margin: 0;
        }
        #navwrapper {
          position: fixed;
          top: 0;
          z-index:20000;
          left: 0;
          width: 100%;
          background: rgba(65, 65, 65, .7);
          margin: 0;
          display: block;
        }
        #show {
          display: block;
          float: right;
        }
        #linkwrapper {
          float: right;
        }
        .pagelink {
          color: red;
          width: auto;
          padding: 0;
          font: 18px arial;
          margin-right: 5px;
          margin-left: 2px;
          float: none;
        }
        #pagelink:hover {
          background: none;
          opacity: .6;
        }
        #hide {
          float: right;
        }
        a {
          float: left;
          padding: 22px;
          text-decoration: none;
          color: white;
          font: 20px 'Mukta';
          transition: all ease .4s;
        }
        a:hover {
          background: rgba(89,89,89,.6);
          color: #FF6161;
        }
        #icon {
          width: 50px;
          height: 50px;
          padding: 14px;
          cursor: pointer;
          float: left;
        }
        #icon:hover {
          background: rgba(89,89,89,.6);
        }
        ul {
          float: left;
          display: block;
          margin: 0;
          z-index:30000;
        }
        li {
          float: left;
          list-style: none;
          margin: 0;
        }
        li a:hover {
          background: rgba(255,255,255,.1);
        }
        #hambutton {
          float: right;
          width: 40px;
          height: 40px;
          display: none;
        }

  /* |||||||| This is the mobile version styling of the Navigation-Bar |||||||| */

        @media only screen and (max-device-width: 480px) {
          #linkwrapper {

          }
          #hambutton {
            float: right;
            display: block;
            margin-top: 12px;
            margin-bottom: 0;
            margin-right: 15px;
            color: white;
            cursor: pointer;
          }
          #hide {
            display: none;
          }
          #show {
            display: block;
            float: left;
            top: 4em;
            width: 100%;
            z-index: 1000;
          }
          ul {
            position: absolute;
            left: 0;
            margin: 0;
            padding: 0;
            width: 100%;
            z-index: 1000;
            background: rgba(89,89,89,.6);
          }
          #navbar {
            display: block;
          }
          li a {
            color: white;
            width: 100%;
            margin: 0;
            padding: 0;
            text-align: center;
            padding-top: 20px;
            padding-bottom: 20px;
            right: 0;
            float: left;
          }
          li {
            float: left;
            width: 100%;
            margin-left: 0;
            padding: 0;

          }
        }
        `}</style>
    </div>
  );
}

export default Nav;
