import Link from 'next/link';

const Nav = () => (
  <div id="navwrapper">
    <Link href="/">
      <a>J & M Transmission</a>
    </Link>
    <h2 id="hambutton">Ham</h2>
    <div id="linkwrapper">

      <ul id="dropdown">
        <li><Link href="/contact">
          <a>Contact Us</a>
        </Link></li>
        <li><Link href="/">
          <a>About</a>
        </Link></li>
      </ul>
    </div>
    <style jsx global>{`
      body {
        margin: 0;
      }
      `}</style>
    <style jsx>{`
      @import url('https://fonts.googleapis.com/css2?family=Rubik:wght@500&display=swap');
      body {
        margin: 0 !important;
      }
      #navwrapper {
        float: left;
        width: 100%;
        background: rgba(89, 89, 89, .8);
        margin: 0;
      }
      #linkwrapper {
        float: right;
      }
      a {
        float: left;
        padding: 22px;
        text-decoration: none;
        color: white;
        font: 18px 'Rubik';
        transition: all ease .4s;
      }
      a:hover {
        background: rgba(89,89,89,.4);
        color: #FF6161;
      }
      ul {
        float: left;
        display: block;
        margin: 0;
      }
      li {
        float: left;
        list-style: none;
        margin: 0;
      }
      h2 {
        float: right;
        display: none;
      }

/* This is the mobile version styling of the Navigation-Bar */

      @media only screen and (max-device-width: 480px) {
        #linkwrapper {
          float: left;
          top: 4em;
          width: 100%;
          z-index: 1000;
        }
        h2 {
          float: right;
          display: block;
          margin-right: 15px;
          color: white;
          border: 1px solid black;
          cursor: pointer;
        }

        ul {
          position: absolute;
          left: 0;
          margin: 0;
          padding: 0;
          width: 100%;
          display: block;
          z-index: 1000;
          background: white;
        }
        li a {
          color: black;
          width: 100%;
          margin: 0;
          padding: 0;
          text-align: center;
          padding-top: 10px;
          padding-bottom: 10px;
          right: 0;
          border: 1px solid black;
          float: left;
        }
        li {
          float: left;
          width: 100%;
          display: block;
          margin-left: 0;
          padding: 0;

        }
        #linkwrapper {
          display: none;
        }
        #hambutton:hover ~ #linkwrapper {
          display: block;
        }
      }
      `}</style>
  </div>
);

export default Nav;
